mapper.select:
select

  distinct


from cp_package_info




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

select 

from cp_package_info
where PACKAGE_ID = #{packageId,jdbcType=BIGINT}
  and DETAIL_CODE = #{detailCode,jdbcType=VARCHAR}

mapper.delete:
delete from cp_package_info
where PACKAGE_ID = #{packageId,jdbcType=BIGINT}
  and DETAIL_CODE = #{detailCode,jdbcType=VARCHAR}
